// icons componenet for handling all icons used in app .
export const Icon = ({ name }) => {
  switch (name) {
    case "slack":
      return <Slack />;

    case "notion":
      return <Notion />;

    case "jira":
      return <Jira />;

    case "azure":
      return <Azure />;

    case "webpack":
      return <Webpack />;

    case "nginx":
      return <Nginx />;

    case "zoom":
      return <Zoom />;

    case "aws":
      return <Aws />;

    case "add":
      return <Add />;

    default:
      return <Unknown />;
  }
};

